var w = innerHeight;
	b = document.getElementById("size");
	wr= document.getElementById("wr");
	defW=window.innerWidth,h=0,w=defW,m=0;
	// GET CURRENT WIDTH
var	wdt=wr.clientWidth;
	// SET EVENT
window.onresize = getSize;
window.onload = x;
function getSize(){
	// var set = setInterval(inc_width);
	// function inc_width(){
	if(window.innerWidth<=defW && wdt <= 480){
		defW=window.innerWidth;
		wdt++;
		wr.style.width = wdt+"px";
	} else if(window.innerWidth>defW && wdt <= 480){
		defW=window.innerWidth;
		wdt--;
		wr.style.width = wdt+"px";
	}
	b.innerHTML = "Window Height: "+window.innerHeight+"<br/>Window Width: "+defW+", Wrapper Width: "+wdt;
}
function x(){
	wr.style.opacity = 1;
	wr.style.marginTop = 0+"px";
	wr.style.width = wdt+"px";
	getSize();
}
